import React from 'react'
import Movie from '../components/Movie'
import { useParams } from 'react-router-dom'
import { useEffect, useState } from 'react';
import UpdateM from '../components/UpdateM'
import '../styles/Movie.css';
export default function Specific() {


  const [moviex, setMoviex] = useState([]);

   const {id} = useParams()

   useEffect(() => {

    fetch(`https://movies-app-4lwq.onrender.com/movies/${id}`)
    .then(response => response.json()) // Parse the JSON response
    .then(response => {
      setMoviex(response);



    })
    .catch(error => console.error('Error fetching data:', error));
  }, []); 



  return (
    <div >


    <div className='.movie-card'>
      <div className='mt' >
      <img className='col-md-2 image-fluid' src={moviex.poster_url} alt='loading...' />
        </div>

      <div className='col-md-6'> 

        <h1 className=''>{moviex.title}</h1>
        <p> {moviex.overview}</p>
       </div>
        <hr/>
     </div>
        <div className=''>
        <UpdateM moviex={moviex}/>
       </div>


      </div>

  )
}
